mapper.select:
select *
from store

	
		and storeName = #{storeName}
	
	
		and drugId = #{drugId}
	
	
		and batchNo = #{batchNo}
	
	
		and inPrice = #{inPrice}
	
	
		and price = #{price}
	
	
		and amount < amountLowLimit
	
	
		and datediff( day, GetDate(), validDate ) <= validDateWarnDays

select *
from store

	
		and storeName = #{storeName}
	
	
		and drugId = #{drugId}
	

order by inTime desc

select s.drugId,d.itemName,d.spec,d.unit,d.vendor,d.storeClass,d.expenseClass,d.drugFrom,d.drugFunction, s.amount,s.batchNo,s.inPrice,s.price,s.validDate,s.place1,s.amountLowLimit,s.validDateWarnDays,s.customOrderCode,s.id as storeIds, s.price*s.amount as storePrice,s.amount as realAmount,s.price*s.amount as realPrice,d.inPrice as inPriceNew, d.price as priceNew from store s inner join dic_drug d on d.id = s.drugId where d.enabled = 1
and s.storeName = #{storeName}
and s.batchNo = #{batchNo}
and ( d.itemName like CONCAT('%',#{itemName},'%') ) OR ( d.wb like CONCAT('%',#{itemName},'%') ) OR ( d.py like CONCAT('%',#{itemName},'%') )
and d.expenseClass = #{expenseClass}
and d.drugFrom = #{drugFrom}
and s.amount < s.amountLowLimit
order by d.itemName asc
